mapper.select:
SELECT
	*
FROM
	AMTF_VIDEO_COMMENT_PRAISE
WHERE
	VIDEO_ID = #{video_id}
AND
	VIDEO_COMMENT_ID = #{video_comment_id}
AND
	USER_ID = #{user_id}

mapper.delete:
DELETE FROM
	AMTF_VIDEO_COMMENT_PRAISE
WHERE
	VIDEO_ID = #{video_id}
AND
	VIDEO_COMMENT_ID = #{video_comment_id}
AND
	USER_ID = #{user_id}
